void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 0.5;
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 pos = (1.0 - uv + 0.5);
	vec2 cir = ((pos.xy*pos.xy+sin(uv.x*18.0+time)/25.0*sin(uv.y*7.0+time*1.5)/1.0)+uv.x*sin(time)/16.0+uv.y*sin(time*1.2)/16.0);
	float circles = (sqrt(abs(cir.x+cir.y*0.5)*25.0)*5.0);
	vec4 color = vec4(sin(circles*1.25+2.0),abs(sin(circles*1.0-1.0)-sin(circles)),abs(sin(circles)*1.0),1.0);
    fragColor = vec4(color.r * color.g * color.b, 1.0, 1.0, 1.0);
}
