void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord.xy / iResolution.xy) - 0.5;
  uv.x *= iResolution.x / iResolution.y;


  float d = length(uv);
  float ripplePattern = sin(d * 100.0 - iTime) / d - (d* 0.8);


  vec3 waterColor = vec3(0.0, 0.0, 0.5);
  vec3 rippleColor = vec3(0.0, 0.5, 0.5);
  vec3 anim = mix(waterColor, rippleColor, ripplePattern);



  fragColor = vec4(anim, 0.0);
}
