void mainImage( out vec4 fragColor, in vec2 fragCoord )
{float angle = iTime*0.01;
vec2 normalizedCoord = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
for(float i = 0.0; i< 19.0; i+=1.0){
 normalizedCoord =abs(normalizedCoord );
 normalizedCoord -=0.6;
  normalizedCoord *=1.3;
  normalizedCoord *= mat2(
  cos(angle),-sin(angle),
  sin(angle),cos(angle)
  );
}

    fragColor = vec4(length(normalizedCoord+vec2(-.5,-0.7)),length(normalizedCoord+vec2(0.2,-0.3)),length(normalizedCoord+vec2(-0.4,-0.1)),1.0);
}
