float col(vec2 uv, float t){
     float s = 2.;
    float id = mod(floor(uv*s).x + floor(uv*s).y,2.)-0.5;
    uv = fract(uv*s);

    float c = cos(
            t*id+

        10.*log(length(uv))+
        10.*log(length(uv-1.))+
        10.*log(length(uv-vec2(1,0)))+
        10.*log(length(uv-vec2(0,1)))
        );

    return smoothstep(0.5,0.6,c);
}

void mainImage( out vec4 o, in vec2 fc )
{

    vec2 uv = fc/iResolution.xy - 0.5;
    uv.x *= iResolution.x/iResolution.y;



    vec3 c = vec3(
        col(uv, iTime-0.1),
        col(uv, iTime),
        col(uv, iTime+0.1)
    );

    uv *=2.;

    c -= vec3(
        col(uv, iTime-0.1),
        col(uv, iTime),
        col(uv, iTime+0.1)
    );
    uv *=2.;

    c += vec3(
        col(uv, iTime-0.1),
        col(uv, iTime),
        col(uv, iTime+0.1)
    );


    o = vec4(c, 1.);
}
