#define offset vec4(5.); // 值越大，越模糊

float computerBluGauss(float x,float sigma)
{
    return 0.39894*exp(-0.5*x*x/(0.20*sigma))/sigma*sigma;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
