    vec2 textureSize = iChannelResolution[0].xy; // 纹理大小
    vec4 _offset = offset + textureSize.xyxy;

    vec4 v_uv01 = v_uv0 + vec4(_offset * vec4(1,1,-1,-1)/iResolution.xyxy);
    vec4 v_uv23 = v_uv0 + vec4(_offset * vec4(1,1,-1,-1)*2.0/iResolution.xyxy);

    /* // 详细计算
    vec4 color = vec4(0.0);

     float w0 = computerBluGauss(0.,8.);
