     float w2 = computerBluGauss(2.,8.);

     float sum = w0+w1*2.+w2*2.;
     color += vec4(w0/sum) * texture(iChannel0, v_uv0.xy);  
     color += vec4(w1/sum) * texture(iChannel0, v_uv01.xy);
     color += vec4(w1/sum) * texture(iChannel0, v_uv01.zw);   
     color += vec4(w2/sum) * texture(iChannel0, v_uv23.xy);  
