    */

    vec4 color = vec4(0.0);
	color += 0.4026 * texture(iChannel0, v_uv0.xy);  
    color += 0.2442 * texture(iChannel0, v_uv01.xy);
    color += 0.2442 * texture(iChannel0, v_uv01.zw);  
    color += 0.0545 * texture(iChannel0, v_uv23.xy);  
    color += 0.0545 * texture(iChannel0, v_uv23.zw);


    fragColor = color;
}
