// more readable version below


void mainImage( out vec4 O, vec2 U )
{
    int n = 1 << 5 - int( U *= .5 / floor(iResolution.xy/8.) ), k=0;       // number of left nodes on the given strip

    for( O = vec4(1) ; k<n*n/2; k++ )                                      // foreach curve
        O *= .5+ .5* min( 1., abs( O.a = mix( .5 +     float(k%n) ,        // strip right node index
                                              1. + 2.* float(k/n) ,        // strip left node index
                                              smoothstep(0.,1.,fract(U.x)) // curve shave
                                            ) - U.y * float(n/4)
                                 ) / fwidth(O.a) );                        // AA draw greying cuve
    O = sqrt(O);                                                           // to sRGB
}





