    O = vec4(1);  
    for( i = .5*s-2.; i < 2.; i += s )
        for( j = s-2.; j < 2.; j += s+s )   
            O *= .5+.5* S( abs( v = mix(i,j, S(fract(U.x)) )
                             // v = mix( i+sin(i*iTime)*.2*s, j+sin(j*iTime)*.4*s, S(fract(U.x)) )   
                                -U.y+2.
                              )/fwidth(v) );
    O = sqrt(O);
}

