/** // --- 267 chars

#define S(v) smoothstep(0.,1., v)

void mainImage( out vec4 O, vec2 U )
{
    U = 4.*U/( floor(iResolution.xy/8.)*8. )  - 2.;
    float s = exp2(-floor(2.-U.x)), i, j, v;

    O = vec4(1);  
    for( i = .5*s-2.; i < 2.; i += s )
        for( j = s-2.; j < 2.; j += s+s )
            v = mix(i,j, S(fract(U.x)) ) - U.y,      
         // v = mix( i+sin(i*iTime)*.2*s, j+sin(j*iTime)*.4*s, S(fract(U.x)) ) - U.y,      
            O *= .5+.5* S( abs(v)/fwidth(v) );

    O = sqrt(O);
}

