{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

}

const vec4 RED = vec4(1.0, 0.0, 0.0, 1.0);
const vec4 BLUE = vec4(0.0, 0.0, 1.0, 1.0);

vec4 update(in ivec2 int_pos, in ivec2 dxdy, in vec4 fragColor) {
    if(fragColor == BLUE || fragColor == RED) {
        return fragColor;
    }

    vec4 other_color = texelFetch(iChannel0, int_pos + dxdy, 0);
    if(other_color == RED) {
        return fragColor;
    }
    if(other_color == BLUE) {
        return BLUE;
    }
    return fragColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 int_pos = ivec2(fragCoord);
    fragColor = texelFetch(iChannel0, int_pos, 0);
    if(iTime > 10.0 && int_pos.x == 0 && int_pos.y == 0) {
        fragColor = BLUE;
    }

    if(iMouse.z > 0.0 && length(iMouse.xy - fragCoord) < 15.0) {
        fragColor = RED;
    }

    fragColor = update(int_pos, ivec2(-1,  0), fragColor);
    fragColor = update(int_pos, ivec2( 1,  0), fragColor);
    fragColor = update(int_pos, ivec2( 0, -1), fragColor);
    fragColor = update(int_pos, ivec2( 0,  1), fragColor);
}
