void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= (iResolution.x / iResolution.y);

    uv = vec2(uv.y, uv.x);

    uv.x += 1.0 * (iResolution.x / iResolution.y);


    float a = atan(uv.y, uv.x * 3.141592);
    float r =  sin(iTime * 0.2) / length(uv);

    float c = 1.0;
    uv = vec2(r * c, a * c);

    uv += vec2(sin(iTime * 0.2), cos(iTime * 0.2));

    vec3 color = vec3(0.0);

    float iterations = 31.0;

    for (float i = 0.0; i < iterations; ++i)
    {

        float fc = length(uv + vec2(cos(sin(iTime / 7.0 + i * 2.0) + sin(uv.x + i * 1.5)), sin(-sin(iTime / 7.0 + i * 3.2) + cos(uv.y + i))));
        float fz = length(vec2(cos(uv.y + iTime + i * 1.1), sin(uv.x - iTime + i * 1.23)));

        color += vec3(sin((uv.x * fz + i) / fc + iTime), cos((uv.y * fz + i) / fc + iTime), 0.0);

    }

    color /= iterations;

    vec3 coloradd = vec3(pow(0.1 / color.r, 2.0), pow(0.1 / color.g, 2.1), pow(0.1 / color.b, 2.5)); 

    color.r += sin(coloradd.r + iTime * 1.2);
    color.g += cos(coloradd.g + iTime * 2.3);
    color.b += tan(coloradd.b + iTime * 3.4);


    fragColor = vec4(color, 1.0);

}
