void circle(vec2 uv, vec2 center, float radius, out vec3 color, vec3 newColor) {
    color = length(uv - center) < radius ? newColor : color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;



    vec3 color;
    circle(uv, vec2(0.0, 0.0), 0.1, color, vec3(0.5, 0.6, 0.7));
    circle(uv, vec2(0.05, 0.05), 0.1, color, vec3(1.0, 1.0, 0.0));

    fragColor = vec4(color, 1.0);
}
