float Circle(float d){

    d -= 0.20;
    d = abs(d);
    d = smoothstep(0.0,0.1,d);
    return d;

}

vec3 pallete( float t){
    vec3 a = vec3(0.000,0.000,0.000);
    vec3 b = vec3(1.000,0.000,0.000);
    vec3 c = vec3(1.000,0.000,0.584);
    vec3 d = vec3(0.212,0.031,0.867);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;
    uv.x *= iResolution.x/iResolution.y;

    vec2 ogUV = uv;

    uv.x += sin(iTime);
    uv.y += cos((iTime + 10.0) * 1.) / 2.0;

    float tD = 1.0;

    vec3 color = vec3(0.169,0.086,0.173);
    color -= vec3(abs(sin(ogUV.x + iTime * 5.0)),0.5, sin(0.5 + iTime * 5.0) * ogUV);

    for(float i = -10.0; i < 10.0; i += 1.0){

        for(float j = -10.0; j < 10.0; j += 1.0){

            vec2 nUV = vec2(uv.x - 0.6 * i, uv.y);
            float c = length(nUV - .2 + 0.2 * j);
            c = Circle(c);
            tD *= c;
        }
    }



    vec3 tDvec = vec3(tD);
    tDvec -= color;

    // Output to screen
    fragColor = vec4(tDvec,1.0);
}
