void mainImage(out vec4 fragColor, in vec2 fragCoord) {
     vec2 p = fragCoord.xy / iResolution.xy;
    vec2 uv = p*vec2(iResolution.x/iResolution.y,1.0);
    float t = iTime*0.5;
     vec2 a = 0.5+0.5*sin(uv*2.0*3.14159+t*3.0);
    float d = cos(length(a-uv.yx*uv.xy));
    float c = sin(smoothstep(1.5,0.2,d));
    float e = sin(smoothstep(0.1,0.0,d));
    float f = sin(smoothstep(1.2,0.0,d));
    fragColor = vec4(f/e*e,f/c*c,f+c,1.0);
}
