// FluidLight Rays
// Based off of a shader by Danilo Guanabara (@Danguafer on shadertoy)

#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=0;i<100;i++) {
		vec2 uv,p=fragCoord.yx/r;
		uv=p;
		p-= uv.x + .1;
		p.x*=r.x/r.y;
		z+=.07;
		l=length(p);
		uv+=p/l*(cos(z)+1.)*(cos(l*50.-z));
		c[i]=.01/length(mod(uv,1.)-.5);
	}
	fragColor=vec4(c/l*1.,t*2.);
}
