vec2 square_uv(vec2 coord, vec2 res) {
    if (res.x >= res.y) {
        coord.x = (coord.x + (res.y - res.x) / 2.0) * res.x / res.y;
    } else {
        coord.y = (coord.y + (res.x - res.y) / 2.0) * res.y / res.x;
    }

    return coord / res;
}

void mainImage(out vec4 outp, in vec2 coord) {
    vec2 uv = square_uv(coord, iResolution.xy);

    outp = texture(iChannel0, uv);
    outp *= step(0.0, uv.x) * step(uv.x, 1.0) * step(0.0, uv.y) * step(uv.y, 1.0);
}
