// original https://twitter.com/zozuar/status/1643974428940992512?s=20

mat2 Rotate(float a) {
  float s = sin(a);
  float c = cos(a);
  return mat2(c, -s, s, c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   float s,t=iTime;
   vec4 o;
   vec3 q,p, d = vec3((fragCoord.xy-iResolution.xy)/iResolution.y, .7); 
   q.zy -= 1.;
   q.xz -= sin(iTime)*.4;
   q.xy *= Rotate(sin(iTime)*.09);


   for(float i,l,v; i++<55.; v=max(s,p.z*v)){
       o += ( i>45. ? d/=d , log(v += 1e-4)*o : vec4(6,8,2,0) / exp(v*1e3)) /3e2;
       p=q-=d*v;
       v=.1;

       for(int j; j++<4; p.xy=fract(p.xy+p.x)-.5) 
           p = vec3(log2(v*=l=length(p.xy))-p.z/l*.6-t*.01,
           atan(p.y,p.x)*2.,
           p.z/l+log(2.5+l)),
           o.r -= p.z*.004,
           o.b -= p.x*.00003;;

   }
   o.b += p.z*.0006; 
   o.r += q.x*.2; 
   o.g += q.y*.02; 


    // Output to screen
    fragColor = o;
}
