const float PI = 3.14159265358;

float chladni(vec2 uv) 
{
    float t = iTime;
    float c = sin(PI * t) * (cos(3.0 * PI * uv.x) * cos(2.0 * PI * uv.y) - cos(2.0 * PI * uv.x) * cos(3.0 * PI * uv.y));
    return c * 0.5 + 0.5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy - 0.5;
    vec3 col = vec3(chladni(uv));
    col *= col * col;
    fragColor = vec4(col,1.0);
}
