#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.141592
float heart(float s,vec2 offset, vec2 st){
	float sign1=(st.x>offset.x)?(1.):(-1.);
    float sign2=(st.y>offset.y+.1)?(1.):(-1.);
    float cor=(st.y>offset.y+.1)?(0.):(-PI);
    float x=sign1*asin(pow((sign1*(st.x-offset.x)/s/16.),1./3.))+cor;
	float y=s*(13.*cos(x)-5.*cos(2.*x)-2.*cos(3.*x)-cos(4.*x))+offset.y;
    float plot=smoothstep(y,y-sign2*2./iResolution.y,st.y);
    return plot;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec2 offset=vec2(0.);
    vec3 color = mix(vec3(0.),vec3(1.),heart(0.02,offset,st));
    fragColor = vec4(color,1.0);
}
