void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(.0);

    float a = 1.0;
    float r = .2;
    float w = .01;

    float d = smoothstep(r, r - w, length(uv));
    col += vec3(1.0, 0.0, .0) * d;
    d = smoothstep(r - 2.0 * w, r - w, length(uv));
    col *= d;

    if (length(uv) < r - w) {
        float l = length(uv - vec2(uv.x, a * uv.x));
        d = smoothstep(.0, w, l * cos(atan(a)));
        col += vec3(1.0, .0, .0) * (1.0 - d);
    }

    fragColor = vec4(col,1.0);
}
