void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;


    float r = 0.06;

    float xydis = r * sqrt(8.0) / 2.0;

    vec3 col = vec3(0.0);

    float not_magic_number = 1.5 / iResolution.y;

    for (float i = 0.0; i < 2.0 + xydis; i += xydis) {
        for (float j = 0.0; j < 2.0 + xydis; j += xydis) {
            float acc = r - distance(vec2(i, j), uv);
            col.gb += smoothstep(1.5 / iResolution.y, 0.0, abs(acc));
        }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
