// Original
// https://www.shadertoy.com/view/MtScWW
float map(vec3 p)
{
    p=abs(mod(p,2.))-2.*.5;
    return length(p)-.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
    vec3 cPos=vec3(sin(iTime),0.,iTime);
    vec3 cDir=normalize(vec3(0)+cPos);
    vec3 cUp=vec3(0,1,0);
    vec3 cSide=cross(cDir,cUp);
    vec3 ray=normalize(cSide*uv.x+cUp*uv.y+cDir);
    float dist=1.;
    float hit=0.;
    float ac=0.;
    vec3 col=vec3(0);
    vec3 rp=cPos+ray*dist;
    for(int i=0;i<200;i++)
    {
        if(dist<.01||hit>80.)break;
        dist=map(rp);
        dist=max(abs(dist),.02);
        rp=cPos+ray*hit;
        ac+=exp(-dist*20.);
        hit+=dist*.5;
    }
    col = vec3(ac * 0.01);
    fragColor=vec4(col,1.0);
}
