vec3 drawRectangle(vec2 fragCoord, vec2 pos, float width, float height, vec3 color, vec3 currentColor)
{

    if(fragCoord.x > pos.x && 
       fragCoord.x < pos.x + width && 
       fragCoord.y > pos.y && 
       fragCoord.y < pos.y + height)
        return color;

    return currentColor;    
}

vec3 drawBuilding1Window1(vec2 fragCoord, vec3 building, float buildPosX)
{
    float windowHeight = 80.0;
    float windowWidth = 40.0;

    return drawRectangle(fragCoord, vec2(buildPosX + 10.0, 0.0), windowWidth, windowHeight, windowsColor, building);
}

