{
    float buildingHeight = 400.0;
    float buildingWidth = 200.0;

    float buildPosX = iResolution.x - mod(iTime * 210.0, iResolution.x * 2.1);

    return drawRectangle(fragCoord, vec2(buildPosX, 0.0), buildingWidth, buildingHeight, building1Color, currentColor);
}

