void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float tol = 0.001;

    // Time varying pixel color
    vec3 col = mix (darkBlue, lightBlue,  sqrt(uv.y) + 0.1);

    float groundSize = 0.15;

    vec2 wheel_1Center = vec2(160.0, 100.0 + sin(iTime* 2.0) * 5.0);    
    vec2 wheel_2Center = vec2(500.0, 100.0 + sin(iTime* 2.0) * 5.0);


    // Output to screen

    float wheelRadius = 60.0;
    float hubCapRadius = 45.0;

    float carHeight = 100.0;

    float carWidth = 600.0;

    vec2 carPos = vec2(45.0, 100.0);


    col = drawBuilding1(fragCoord, col);

    col = drawBuilding2(fragCoord, col);

    col = drawBuilding3(fragCoord, col);    

    col = drawBuilding4(fragCoord, col);

    col = drawRectangle(fragCoord, carPos, carWidth, carHeight, carColor, col);

    vec2 upperCarPos = vec2(160.0, 200.0);

    float upperCarWidth = 340.0;
    float upperCarHeight = 100.0;

    col = drawRectangle(fragCoord, upperCarPos, upperCarWidth, upperCarHeight, carColor, col);

    col = drawRectangle(fragCoord, upperCarPos + 10.0, upperCarWidth - 20.0, upperCarHeight - 20.0, windowsColor, col);

    if(fragCoord.y < 50.0)
        col = gray;

    if( length(fragCoord - wheel_1Center) < wheelRadius)
    {
        if(length(fragCoord - wheel_1Center) < hubCapRadius)
           col = silver;
        else
            col = black;
    }   

    if(length(fragCoord - wheel_2Center) < wheelRadius)
    {
        if(length(fragCoord - wheel_2Center) < hubCapRadius)
            col = silver;
        else
            col = black;
    }


    vec2 q = uv - vec2(0.4,0.35);

    float r = 0.05 + 0.024 * cos( atan(q.y,q.x) * 6.0 - 3.0);

    if(length(q) < r - 0.001)
        col = vec3(1.0);


    fragColor = vec4(col,1.0);

}
