#define scattering 4.0
#define time iTime*3.0
#define position sin(time)/2.0
#define scattering2 scattering*position-(scattering/2.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        // Normalized pixel coordinates (from 0 to 1)
        vec2 uv = fragCoord/iResolution.xy;
        vec2 p = uv * scattering -scattering2;
        float y =  1.0 - uv.y;
        float c  = 1.0 - smoothstep(0.0, y , abs(p.x) );
        // Output to screen
       fragColor = vec4(c,c,c,1.0);
}
