void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float n_mouse_x_normalized = iMouse.x / iResolution.x; 
    //float n_index_pixel = float(fragCoord.x) + float(fragCoord.y)*float(iResolution.x);
    float n_index_pixel = fragCoord.y * iResolution.x + fragCoord.x;
    float n_brightness = mod(iTime + n_index_pixel*0.01*n_mouse_x_normalized, 1.0);

    // Output to screen
    fragColor = vec4(
        n_brightness,
        n_brightness,
        n_brightness,
        1.0
    );
}
