#define rot(a) mat2(cos( a+ vec4(0,33,11,0) ))
mat3 pl(float t, vec2 u) {
    return cos(t)*mat3(u.x, u.y, tan(t), .5, .4, max(vec2(.2), cos(u)), .2, cos(sin(t)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 rd = vec3(uv, .7);
    rd.xy *= rot(sin(iTime));
    rd.xz *= rot(cos(iTime));
    // Time varying pixel color
    vec3 col = texture(iChannel1, rd).rgb;
    col *= pl(iTime, uv);
    col += vec3(cos(uv), sin(iTime)) * .7;
    col /= texture(iChannel0, cos(uv)).rgb;
    col = clamp(col, .2, .7);
    if (col == vec3(.2) || col == vec3(.7)) {
        col *= texture(iChannel0, cos(uv)).rgb * vec3(cos(uv), uv.y);
    }    

    // Output to screen
    fragColor = vec4(col,1.0);
}
