float noise(vec2 st) {
    return fract(sin(dot(st, vec2(12.9898, 78.233))) * 43758.5453123);
}

float noise2(vec2 co, float seed)
{
    vec4 s = vec4(seed, seed, seed, seed);
    seed = fract(sin(dot(s.xyzw, vec4(123456789.0, 362436069.0, 521288629.0, 88675123.0))) * 43758.5453);
    return fract(sin(dot(co.xy + s.xz ,vec2(12.9898,78.233))) * 43758.5453);
}

// Define the main function for the shader, which will be called once for each pixel on the screen
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Calculate the current position of the pixel in the coordinate space of the shader
    vec2 st = fragCoord.xy/iResolution.xy;

    // Get noise for prev frame and current
    float n1 = noise(st * (iTime - iTimeDelta));
    float n2 = noise(st * iTime);

    // Blend between prev & current -> animated
    vec4 c1 = vec4(n1, n1, n1, 1.0);
    vec4 c2 = vec4(n2, n2, n2, 1.0);


    fragColor = mix(c1, c2, n1);
}
