float sdCircle(vec2 uv, float r)
{
    return length(uv)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 centerCircle = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = (2.*iMouse.xy-iResolution.xy)*-1. / iResolution.y;

    vec3 col = vec3(uv.x);

    vec3 colCircle = mod(centerCircle.x * 6. + vec3(0., 4., 2.), 6.);
    colCircle = clamp(abs(colCircle - 3.)-1., 0., 1.);
    colCircle = mix(vec3(uv.x/2.), colCircle, smoothstep(0., 0.8, uv.y));

    float circle = sdCircle(centerCircle+mouse, 0.3);
    circle = step(circle, 0.);

    col = mix(col, colCircle, circle);

    fragColor = vec4(col,1.0);
}
