void mainImage( out vec4 O, in vec2 u )
{
    vec2 r = iResolution.xy;
    u = (u - .5 * r) / r.y;

    float t = .1 * iTime,
          i = .3;

    for (i; i < 2.7; i += .3) {
        t /= i;
        u = 4. * u * mat2(u.x+u.y, u.x-u.y, u.y-u.x, u.x+u.y)
          + .3 * sin(1.9 * t) + 2. * vec2(cos(t), sin(t));
        u /= dot(u, u);
    }
    O = texture(iChannel0, u/2e2) 
      + .15 / cosh(3.*abs(fract(4.*u.x)-.5));
}
