void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 cFront=vec3(1.,0.,0.);
    vec3 cBack=vec3(0.,0.,1.);

    vec2 center=iResolution.xy/2.;
    float dis=distance(fragCoord,center);
    float rds=0.45*iResolution.y;

    float smth=smoothstep(rds-1., rds+1., dis);
    vec3 clr=mix(cFront,cBack,smth);
    fragColor=vec4(clr,1.);
}
