void mainImage( out vec4 o, in vec2 u )
{
    vec2 z;
    vec3 c;
    for ( int i = 0; i < 128; i++ ) {
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + (u * 2. - iResolution.xy) / iResolution.y * 1.2;
        float p = mod(float(i)/6.+iTime, 6.);
        if (length(z) < 2.) { c = vec3(0); } else { c = vec3(abs(p-3.)-1., -abs(p-2.)+2., -abs(p-4.)+2.); break; }
    }
    o = vec4(c, 1);
}
