mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

float variation(vec2 v1, vec2 v2, float strength, float speed,float seed) {
	return sin(
        dot(normalize(v1), normalize(v2)) * strength + iTime * speed + seed) / 15.0;
}

vec3 paintCircle (vec2 uv, vec2 center, float rad, float width,float seed,float up) {

    vec2 diff = center-uv;
    float len = length(diff);

    len += variation(diff, vec2(0.0, 1.0), 5.0, up,seed);
    len += variation(diff, vec2(1.0, 0.0), 5.0, up,126.0);

    float circle = smoothstep(rad, rad-width, len) - smoothstep(rad, rad - 20.0, len);
    return vec3(circle);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= 1.5;
    uv.x -= 0.25;

    vec3 color;
    float radius = 0.3;
    vec2 center = vec2(0.5);
    color = paintCircle(uv, center, radius, 0.3,12.0,4.0);
    color.r *= color.r;
	fragColor = vec4(color, 1.0);
}
