void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    int numPoints = 7; // You can change the number of points of the star
    float innerRadius = 0.2;
    float outerRadius = 0.5;
    float pulsationSpeed = 2.0;

    float angle = atan(uv.y, uv.x);
    float radius = length(uv);
    float halfSegment = 3.14159265359 / float(numPoints);
    float segmentPercentage = mod(angle + halfSegment, 2.0 * halfSegment) / halfSegment;

    float pulsatingRadius = mix(innerRadius, outerRadius, (sin(iTime * pulsationSpeed) + 1.0) / 2.0);
    float starRadius = mix(innerRadius, pulsatingRadius, step(0.5, segmentPercentage));
    float starMask = smoothstep(starRadius - 0.01, starRadius, radius);

    vec3 color = vec3(1.0 - starMask);
    fragColor = vec4(color, 1.0);
}
