float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}


#define BLUR_FACTOR 0.01
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    if(iFrame==0){
        fragColor = texture(iChannel0, uv);
        return;
    }
    vec2 off = vec2(
        hash11(iTime*100.) * BLUR_FACTOR,
        hash11(iTime*100.35) * BLUR_FACTOR
    );


    fragColor = mix(texture(iChannel0, uv + off), texture(iChannel1, uv), 0.99);

}
