#define iter 2.
#define distortScale 20.
#define distortPower 0.1
#define Speed 1.

#define BOTTOM 0.1
#define TOP 0.9
#define LEFT 0.01
#define RIGHT 0.99

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    vec3 col = vec3(0.);

    vec3 finalCol = vec3(0,0,0);
	float halfDistort = distortScale / 0.5;
	float distortsc2 = distortScale / distortScale + halfDistort;

	for(float i = 1.0; i < iter; i++){
		uv.x += distortPower / i * cos(i * distortScale * uv.y - iTime * Speed);
		uv.y += distortPower / i * cos(i * distortsc2 * uv.x + iTime * Speed);
	}


    vec4 barsxy = vec4(BOTTOM, TOP, LEFT, RIGHT);

    if( uv.y < barsxy.x || uv.y > barsxy.y || uv.x < barsxy.z || uv.x > barsxy.w ) {
        col = vec3(1.,1.,1.);
    }


    fragColor = vec4(col,1.0);
}
