// Attempt at porting beesandbombs motion blur to GLSL/Shadertoy

float TAU = 2.0*3.14159256;

// SDF Functions from IQ
float circleSDF(vec2 coords, float rad, vec2 offset){
  return (length(coords-offset) - rad);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5* iResolution.xy ) /iResolution.y;  

    // Blur Parameters
    float samples = 10.0;
    float spacingFactor = .02;

    vec3 result = vec3(0.);

    float fracTime = fract(iTime);

    // Loop Over the number of samples
    for(float sa = 0.; sa < samples; sa++)
    {
      float tt = fracTime + sa*spacingFactor/samples;

      // Animation Code Start
      vec3 col = vec3(0.);
      float nc = 3.0;
      for(float numC = 0.; numC < nc; numC++){
        float cSDF = circleSDF(uv, .05, vec2(.4*sin(2.*TAU*tt+numC*TAU/nc),.4*cos(3.*TAU*tt+numC*TAU/nc)));
        col += smoothstep(.002,-.002,cSDF);
      }
      // Animation Code End

      // Add each sample into the into the result
      result += col;                
    }

    // Divide the results by the number of samples
    vec3 colOut = result*1.0/samples;

    fragColor = vec4(colOut,1.0);
}
