void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;
    vec2 q = p - vec2(0.5,0.5);

    float r = 0.1 + 0.1*cos(q.x*100. + cos(sin(q.x) *iTime)*5.);
    vec3 col = vec3( (sin(iTime)+0.3)*clamp(abs(0.5 - q.x), 0.1, 0.5),
    clamp(cos(iTime+sin(q.x*q.y)),0.3,0.8) , 
    (cos(iTime)+0.3)*0.15+q.yx);

    col -= smoothstep(r, r+0.5,length(q));

    // Output to screen
    fragColor = vec4(col,1.5);
}
