const float   a   = 1./3.;
const float   b   = 1./6.;
const float scale =  .000000000000000005 ;


vec3 palette(float t) {

    vec3 a = vec3( 1.2,  1.7,     .4    );
    vec3 b = vec3( .5,  1.8,     9.5    );
    vec3 c = vec3(100. , 6.,     500.     );
    vec3 d = vec3( .0,  9999.33333, 8.66666);

    return a + b*sin(6.28318*(c*t + d));
}



vec3 grayScale(float t) {

    return vec3(t/2.,t*t,1./t);
}


float f(float x, float y, float iTime) { 

  float d =  (x*x+y/x)/(y+x/y) + y*y*x/(x-y);
  float n=0.;

  //float pa=a*log(iTime/3.);
  //float pb=b*log(iTime/2.);


  //float pa=a*10.*sin(iTime/7.);
  //float pb=b*20.*cos(iTime/5.);

  float pa=a*2.*sin(iTime/.2);
  float pb=b*3.*cos(iTime/.3);


  for(int i=0; i<20; i++) {

      float newX = ( x  +  pa/d)*x - y*y;
	  float newY = (2.*y + pb/d)*y;  

	  x = newX;
	  y = newY;	  
	  n+=1.;

      // sentinela
      //if(n>10.) break;

      d =  y*x;
      if(d >= 20.) break;
  }

  return (1.-.1/(cos(d/100.)+1.)*.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = scale*log(iTime)/sin(iTime/1.)*(fragCoord/iResolution.xy - .5);

    float x = uv.x/.000000000001;
    float y = uv.y/.0000000000001;


    float valxy = f(y,x, iTime*.05);

    //vec3 col = grayScale(valxy);
    //vec3 col = palette(valxy);
    vec3 col = palette(valxy)*(1.-grayScale(valxy));


    // Output to screen
    fragColor = vec4(col,1.0);
}
