#define PI 3.145
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //http://dev.thi.ng/gradients/
    float zoom = 10.0;
    vec2 uv = fragCoord/iResolution.xy;
    vec3 fases =vec3(0.929,0.737,0.208)*uv.x*zoom*PI;
    vec3 off =vec3(0.329,0.329,0.329);
    vec3 amp =vec3(0.961,0.749,0.380);
    vec3 vel =vec3(0.969,0.314,-0.596)* iTime *1.;

    vec3 pal = sin(vel+fases)*amp+off;

    // Output to screen
    fragColor = vec4(pal,1.0);
}
