// Code by Flopine

// Thanks to wsmind, leon, XT95, lsdlive, lamogui, 
// Coyhot, Alkama,YX, NuSan, slerpy, wwrighter 
// BigWings, FabriceNeyret and Blackle for teaching me

// Thanks LJ for giving me the spark :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and other to sprout :)  
// https://twitter.com/CookieDemoparty


#define PI acos(-1.)

#define AAstep(thre, val) smoothstep(-.7,.7,(val-thre)/min(0.07,fwidth(val-thre)))
#define circle(u,s) (length(u)-s)
#define square(u,s) (max(abs(u.x),abs(u.y))-s)

#define noise(u) textureLod(iChannel0, u, 0.).x


void mo (inout vec2 p, vec2 d)
{
    p = abs(p)-d;
    if (p.y>p.x) p=p.yx;
}

float truchet (vec2 uv)
{
    vec2 id = floor(uv);

    if(noise(id*0.21+iTime*0.001)<.5) uv.x *= -1.;

    uv = fract(uv)-.5; 
    vec2 uu = uv;

    float s = (uv.x>-uv.y) ? 1. : -1.;   
    uv -= s*0.5;
    float flow = mod(id.x+id.y,2.)*2.-1.,
    anim = (PI/2.)*(floor(iTime)+ pow(fract(iTime), 5.));
    float a = atan(uv.x, uv.y)*flow+anim;
    a = cos(a*4.)*.5+.5;
    float w = mix(0.05, 0.15, a);
    float d = AAstep(w, abs(circle(uv, 0.5)) );

    uv = uu;
    if (noise(id*0.32)<.5) d *= AAstep(0.08, abs(uv.x+uv.y));
    if (noise(id*0.46)<.5) d *= AAstep(0.05, abs(-uv.x+uv.y));

    mo(uv, vec2(0.5));
    d *= AAstep(0.001,square(uv, 0.1));

    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    uv *= 3.;
    uv.y += iTime;
    vec3 col = mix( vec3(1.), vec3(0.7, 0.1,0.1), truchet(uv) );

    fragColor = vec4(sqrt(col),1.0);
}
