void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int m = tz+1;
    int i = iFrame%m;
    //if(i!=0){discard;}  //comment out to see more frames but more flicker too
    vec4 a = texelFetch(iChannel0,ivec2(fragCoord),0);
    uint n = (1U<<m)-1U;
    uint b = floatBitsToUint(a.x)&n;
    uint c = ((b>>i)|(b<<(m-i))) &n;
    fragColor = vec4(float(c)/float(n));
}

#define sz  5.   //size of kernel across space
#define tz  5    //size of kernel across time

