// Distance to a rounded box in the L-infinity metric space
//
// List of some other 2D distances in L-infinity:
//
// https://www.shadertoy.com/playlist/XXccDH


float sdRoundBox( in vec2 p, in vec2 ra, in float rb )
{
    p = abs(p) - ra;
    p = max(p,p.yx-rb);
    float a = 0.5*(p.x+p.y);
    float b = 0.5*(p.x-p.y);
    return a - sqrt(rb*rb*0.5-b*b);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    float ratio = iResolution.x / iResolution.y;
//    if( iMouse.z<0.01 ) m=sin(-0.17*1.0*vec2(1.1,1.3)+vec2(0,2));

    float rad = abs(sin(iTime)) * 1.0;
    vec2  wid = vec2(ratio - rad,1.0 - rad);

    float d = sdRoundBox(p, wid, rad);

    vec3 col = vec3(1.0,0.9,1.0) + sign(d)*vec3(-0.3,0.4,0.3);
	// col *= 1.0 - exp(-3.0*abs(d));
	// col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0, 0.0, 0.0), 1.0-smoothstep(0.0,0.008,abs(d)) );
	fragColor = vec4(col,1.0);
}
