#if 0
// NOTE: t in [-1,+1], unstable near zero
float circular_connector(vec2 p, float t/*offset*/) // original by jt
{
    //p.x = abs(fract(p.x/4.0)-0.5)*4.0-1.0; // repetition
    //p.x = 1.0 - abs(p.x); // bump

    p.x *= sign(t); t = abs(t); // workaround for negative offset

    float r = (t*t+1.0)/(2.0*t); // radius
    vec2 c0 = vec2(-1,-t+r); // center
    vec2 c1 = vec2(1,t-r); // center
    float d0 = length(p-c0)-r; // circle
    float d1 = length(p-c1)-r; // circle
    if(p.x > 1.0) return min(t-p.y, d0); // line
    if(p.x < -1.0) return -min(t+p.y,d1); // line
    vec2 d = c1 - c0; // connection
    vec2 e = vec2(d.y,-d.x); // orthogonal
    if(dot(e, p) >= 0.0) return d0; // use distance on one side
    return -d1; // use inverted distancee on other side
}
#else
// NOTE: t in [-1,+1], unstable near zero
float circular_connector(vec2 p, float t/*offset*/) // optimized by iq
{
    if( t<0.0 ) { t=-t; p.x=-p.x; } // line can be removed if t always in [0,+1]

    float r = (t*t+1.0)/(2.0*t);
    float s = (p.y-p.x*(t-r)<0.0) ? -1.0 : 1.0;
    p *= s;
    float z = (p.x<1.0) ? -1.0 : 1.0;
    return z*s*min(t-z*p.y, length(p-z*vec2(-1,-t+r))-r);
}
#endif
float map(vec2 p)
{
    float t = cos(iTime*0.5);
    //float t = -0.5;
    return circular_connector(p*2.0, t)/2.0;
}

#define EPSILON 0.001

// part of sdf error detection (jt/spalmer)
vec2 gradient(vec2 p) // NOT normalized so we can use it's length to detect errors in the SDF.
{
    vec2 h = vec2(EPSILON,0);
    return (vec2(map(p+h.xy) - map(p-h.xy),
                 map(p+h.yx) - map(p-h.yx)))/EPSILON/2.0;
}

#define pi 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float d = map(p);

    // using iq's sdf visualization
    vec3 col = (d<0.0) ? vec3(0.6,0.8,1.0) : vec3(0.9,0.6,0.3);
    col *= 1.0 - exp(-9.0*abs(d));
	col *= 1.0 + 0.2*cos(128.0*abs(d));
	col = mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)));

    // sdf error detection (jt/spalmer)
    {
        // Jump the distance given by the SDF at the current position
        // along the gradient given by the SDF at the current position
        // (direction to the nearest surface) and evaluate the SDF there.
        // Because the SDF is supposed to describe the minimal distance to the surface,
        // the result should be (close to) zero. (translation from math to words: jt)
        float error = map(p - gradient(p) * map(p)); // SDF error detection suggested to me by spalmer - thanks!
        //col += smoothstep(0.0,sqrt(EPSILON), abs(error)) * (cos(iTime*2.0*pi)*0.5+0.5); // detect both interior and exterior errors (NOTE: false alarms on some shapes)
        col.r += smoothstep(0.0,/*sqrt*/(EPSILON), abs(error)) * (cos(iTime*2.0*pi)*0.5+0.5); // detect both interior and exterior errors (NOTE: false alarms on some shapes)
    }

    if(iMouse.x > 0.001)
    {
        // using iq's sdf visualization
        d = map(m);
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0015));
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col, 1.0);
}
