void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(
    sin((iTime * 1.9 + (uv.x * 5.0)) * (5.0 + iTime / 3.0)) * sin(iTime*0.02),
    cos((iTime * 2.0 + (uv.x * 4.0)) * (4.0 + iTime / 4.0)) * cos(iTime*0.03),
    -sin((iTime * 2.1 + (uv.x * 3.0)) * (3.0 + iTime / 5.0)) * -sin(iTime*0.05));

    // Output to screen
    fragColor = vec4(col,1.0);
}
