vec3 color (float red, float green, float blue) {vec3 color; color[0] = red / 255.0; color[1] = green / 255.0; color[2] = blue / 255.0; return color;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;

    float k = iResolution.x / iResolution.y;
    float correction_val = sin(iTime * 5.0 - 0.2 * 13.0 - uv.y * 13.0);

    /*                                                  // This code can make the flag rotate (because why not?)
    uv[0] = uv[0] - 0.45;
    uv[1] = uv[1] - 0.5;

    uv[0] = uv[0] * k;

    uv = vec2(uv[0], uv[1]) * mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime));

    uv[0] = uv[0] / k;
    uv[0] = uv[0] + 0.45;
    uv[1] = uv[1] + 0.5;
    */

    if (uv.x > 0.35 + sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0) / 156. && uv.x < 0.4 + sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0) / 156.0 || uv.y > 0.45 + sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0) / 56.0 && uv.y < 0.55 + sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0) / 56.0/* + correction_val / 56.*/)
        col = color(250.0, 250.0, 0.0);
    else
        col = color(40.0, 70.0, 220.0);

    col[0] = col[0] - (1. - sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0)) * 0.15;
    col[1] = col[1] - (1. - sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0)) * 0.15;
    col[2] = col[2] - (1. - sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0)) * 0.15;

    if (uv.x > 0.7 + sin(iTime * 5.0 - uv.y * 13.0) / 100.0 || uv.x < 0.2 || uv.y > 0.82 + sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0) / 56.0 - correction_val / 56. || uv.y < 0.2 + sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0) / 56.0)
        col = color(160.0, 220.0, 255.0);

    if (uv.x < 0.2 && uv.x > 0.17 && uv.y < 0.85)
        col = color(50.0, 50.0, 50.0);
    if (uv.x < 0.195 && uv.x > 0.175 && uv.y < 0.85)
        col = color(65.0, 65.0, 65.0);
    if (uv.x < 0.19 && uv.x > 0.18 && uv.y < 0.85)
        col = color(80.0, 80.0, 80.0);

    if ((uv.x - 0.185) * (uv.x - 0.185) * k * k + (uv.y - 0.85) * (uv.y - 0.85) < 0.0015)
        col = color(0.0, 0.0, 0.0);

    fragColor = vec4(col, 1.0);
}
