#define PI 3.1415926

float line(float b, float axis, float blur)
{
    return smoothstep(b-blur, b, axis) - smoothstep(b, b+blur, axis);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;
    vec3 colorA = vec3(0.149,0.141,0.912);
    vec3 colorB = vec3(1.000,0.833,0.224);

    float l, y, x;

    vec3 pct = vec3(uv.x);
    col = mix(colorA, colorB, pct);

    pct.r = smoothstep(0.0, 1.0, uv.x);
    pct.g = pow(uv.x, 0.5);
    pct.b = pow(uv.x, 2.0);

    col = mix(col, vec3(1.0, 0.0, 0.0), line(uv.y, pct.r, 0.01));
    col = mix(col, vec3(0.0, 1.0, 0.0), line(uv.y, pct.g, 0.01));
    col = mix(col, vec3(0.0, 0.0, 1.0), line(uv.y, pct.b, 0.01));

    fragColor = vec4(col,1.0);
}
